import styled from 'styled-components'
import ValidationCheck from './ValidationCheck'
import SearchPostcode from './SearchPostCode'
import Alert from './Alert'

export default function Name() {
  const {
    name,
    nameErr,
    nameErrMessage,
    onChangeName
  } = ValidationCheck()

  const {
    isComplete,
    address,
    handleClick,
  } = SearchPostcode()

  return (
    <InputWrapper>
      <LeftWrapper>
        <LabelText>이름 <Star>*</Star></LabelText>
      </LeftWrapper>
      <MiddleWrapper>
        <InputWrap>
        <InputField
          id='name'
          name='name'
          type='text'
          placeholder='이름을 입력해주세요'
          maxLength={20}
          value={name}
          onChange={onChangeName} />
        </InputWrap>
        {nameErr && <Alert message={nameErrMessage}/>}
      </MiddleWrapper>
      <RightWrapper>
      {isComplete && <Button type="button" onClick={handleClick}>
          <span>
            <img src='https://res.kurly.com/pc/service/cart/2007/ico_search.svg' />
            재검색
          </span>
        </Button>}
      </RightWrapper>
    </InputWrapper>
  )
}

const InputWrapper = styled.div`
  display: inline-flex;
  width: 100%;
  padding: 10px 20px;
`

const LeftWrapper = styled.div`
  width: 139px;
  padding-top: 12px;
`

const MiddleWrapper = styled.div`
  flex: 1 1 0%;
`

const RightWrapper = styled.div`
  width: 120px;
  margin-left: 8px;
  `

  const LabelText = styled.label`
  font-weight: 500;
  color: rgb(51, 51, 51);
  line-height: 20px;
`

  const Star = styled.span`
  color: rgb(238, 106, 123);
`

const InputWrap = styled.div`
  position: relative;
  padding-bottom: 0px;
`

const InputField = styled.input`
width: 100%;
height: 48px;
padding: 0px 11px 1px 15px;
border-radius: 4px;
border: 1px solid rgb(221, 221, 221);
font-weight: 400;
font-size: 16px;
line-height: 1.5;
color: rgb(51, 51, 51);
outline: none;
box-sizing: border-box;
`

const Button = styled.button`
  display: block;
  padding: 0px 10px;
  text-align: center;
  overflow: hidden;
  width: 100%;
  height: 44px;
  border-radius: 3px;
  color: rgb(95, 0, 128);
  background-color: rgb(255, 255, 255);
  border: 1px solid rgb(95, 0, 128);
  span {
    font-weight: 500;
    font-size: 14px;
    display: inline-block;
  }
`
