import styled from 'styled-components'
import SearchPostcode from './SearchPostCode'

export default function Address() {
  const {
    isComplete,
    address,
    handleClick,
  } = SearchPostcode()
  

  return (
    <InputWrapper>
      <LeftWrapper>
        <LabelText>주소 <Star>*</Star></LabelText>
      </LeftWrapper>
      <MiddleWrapper>
        <InputWrap>
          {!isComplete && 
            <SearchAddress onClick={handleClick}>
              <span>
                <img src="https://res.kurly.com/pc/service/cart/2007/ico_search.svg" />주소 검색
              </span>
            </SearchAddress>}
          {isComplete && 
            <>
            <MainAddress>
              <InnerWrapper>
                <InputField
                  name="address"
                  placeholder="주소를 검색해주세요"
                  type="text"
                  value={address}
                  readOnly
                />
              </InnerWrapper>
            </MainAddress>
            <ExtraAddress>
              <InnerWrapper>
                <InputField
                  name="subAddress"
                  placeholder="나머지 주소를 입력해주세요"
                  type="text"
                />
              </InnerWrapper>
            </ExtraAddress>
            </>}
        </InputWrap>
      </MiddleWrapper>
      <RightWrapper>
        {isComplete && <Button type="button">
          <span>
            <img src='https://res.kurly.com/pc/service/cart/2007/ico_search.svg' />
            재검색
          </span>
        </Button>}
      </RightWrapper>
    </InputWrapper>
  )
}

const InputWrapper = styled.div`
  display: inline-flex;
  width: 100%;
  padding: 10px 20px;
`

const LeftWrapper = styled.div`
  width: 139px;
  padding-top: 12px;
`

const MiddleWrapper = styled.div`
  flex: 1 1 0%;
`

const RightWrapper = styled.div`
  width: 120px;
  margin-left: 8px;
  `

  const LabelText = styled.label`
  font-weight: 500;
  color: rgb(51, 51, 51);
  line-height: 20px;
`

  const Star = styled.span`
  color: rgb(238, 106, 123);
`

const InputWrap = styled.div`
  position: relative;
  padding-bottom: 0px;
`

const InputField = styled.input`
width: 100%;
height: 48px;
padding: 0px 11px 1px 15px;
border-radius: 4px;
border: 1px solid rgb(221, 221, 221);
font-weight: 400;
font-size: 16px;
line-height: 1.5;
color: rgb(51, 51, 51);
outline: none;
box-sizing: border-box;
`

const Button = styled.button`
  display: block;
  padding: 0px 10px;
  text-align: center;
  overflow: hidden;
  width: 100%;
  height: 44px;
  border-radius: 3px;
  color: rgb(95, 0, 128);
  background-color: rgb(255, 255, 255);
  border: 1px solid rgb(95, 0, 128);
  span {
    font-weight: 500;
    font-size: 14px;
    display: inline-block;
    img {
      display: inline-block;
      width: 20px;
      height: 20px;
      margin-right: 4px;
      vertical-align: middle;
    }
  }
  &:disabled {
    border-color: rgb(221, 221, 221);
    color: rgb(221, 221, 221);
    cursor: default;
  }
`

const SearchAddress = styled.button`
  display: block;
  padding: 0px 10px;
  text-align: center;
  overflow: hidden;
  width: 100%;
  height: 44px;
  border-radius: 4px;
  color: rgb(95, 0, 128);
  background-color: rgb(255, 255, 255);
  border: 1px solid rgb(95, 0, 128);
  span {
    font-size: 14px;
    font-weight: 500;
    img {
      display: inline-block;
      width: 20px;
      height: 20px;
      margin-right: 4px;
      vertical-align: middle;
    }
  }
`

const MainAddress = styled.div`
  padding-bottom: 12px;
`

const ExtraAddress = styled.div`
  padding-bottom: 0px;
`

const InnerWrapper = styled.div`
  position: relative;
  height: 48px;
`
