import React, {useRef} from 'react'
import styled from 'styled-components'
import {Navigation} from 'swiper'
import { Swiper, SwiperSlide } from 'swiper/react'
import Product from './Product'
import { ProductInformation } from '../fakeRepo/salesProducts/salesProducts'

type Props = {
  items: ProductInformation[],
}
export default function ProductSwiper({ items }: Props) {

  const _prevBtn = useRef(null);
  const _nextBtn = useRef(null);

  return (
    <>
    <StyledSwiper
      slidesPerView={4}
      spaceBetween={10}
      navigation={{
        nextEl: _nextBtn.current,
        prevEl: _prevBtn.current
      }}
      modules={[Navigation]}
    >
      {items.map((item, index) => (
        <SwiperSlide>
          <Product key={index} item={item} />
        </SwiperSlide>
      ))}
    </StyledSwiper>
    <PrevButton ref={_prevBtn}/>
    <NextButton ref={_nextBtn}/>
    </>
  )
}


const StyledSwiper = styled(Swiper)`
  margin-left: -9px;
`

const PrevButton = styled.button`
  position: absolute;
  z-index: 100;
  border: 0px;
  outline: 0px;
  width: 60px;
  height: 60px;
  background: url(data:image/svg+xml;base64,) 50% 50% no-repeat;
  transition: background 0.5s ease 0s;
  top: calc(50% - 50px);
  left: 0px;
  transform: translate(-50%, -50%);
`

const NextButton = styled.button`
  position: absolute;
  z-index: 100;
  border: 0px;
  outline: 0px;
  width: 60px;
  height: 60px;
  background: url(data:image/svg+xml;base64,) 50% 50% no-repeat;
  transition: background 0.5s ease 0s;
  top: calc(50% - 50px);
  right: 0px;
  transform: translate(50%, -50%) rotate(180deg);
`
