import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styled, { css } from 'styled-components';
import Join from './Joins';

interface Container {
  position: string;
  zIndex: string;
  top: string;
  bottom: string;
  left: string;
  right: string;
  width: string;
  height: string;
  wideScreenLeft: string;
  wideScreenMarginLeft: string;
  margin: string;
  marginLeft: string;
  border: string;
  backgroundColor: string;
  paddingRight: string;
  fontWeight: string;
  fontSize: string;
  lineHeight: string;
  background: string;
  scrollFixed: boolean;
}


export default function Header() {
  const navigate = useNavigate();
  const [scrollFixed, setScrollFixed] = useState(false);

  
  const handleScroll = () => {
    if (window.scrollY >= 100) {
      setScrollFixed(true);
    } else {
      setScrollFixed(false);
    }
  }

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll)
  }, []);

  return (
      <HeaderContainer>
        <Join/>
        <HeaderWrapper>
          <Logos>
            <img src="data:image/svg+xml;base64," alt="마켓컬리 로고"/>
            <button
              onClick={() => navigate('/')}>마켓컬리</button>
            <button>뷰티컬리</button>
          </Logos>
          <SearchWrapper>
            <Search scrollFixed={scrollFixed}>
              <SearchInput
                scrollFixed={scrollFixed}
                id="gnb-search" 
                placeholder="검색어를 입력해주세요" 
                required />
              <SearchButton scrollFixed={scrollFixed}
              ></SearchButton>
            </Search>
          </SearchWrapper>
          <HeaderIconsContainer>
            <HeaderIcons scrollFixed={scrollFixed}>
              <LocationIcon></LocationIcon>
              <HeartIcon></HeartIcon>
              <CartIconWrapper>
                <div>
                  <CartIcon></CartIcon>
                </div>
              </CartIconWrapper>
            </HeaderIcons>
          </HeaderIconsContainer>
        </HeaderWrapper>
      </HeaderContainer>
  )
}


const HeaderContainer = styled.div`
  position: relative;
  width: 1050px;
  height: 100px;
  margin: 0px auto;
  letter-spacing: -0.3px;
`

const HeaderWrapper = styled.div`
  height: 100px;
  padding-top: 36px;
`

const Logos = styled.div`
  display: flex;
  align-items: center;
  height: 63px;

  button:first-of-type {
    flex-shrink: 0;
    margin-left: 20px;
    font-size: 18px;
    font-weight: normal;
    line-height: 1.33;
    letter-spacing: normal;
    color: rgb(181, 181, 181);
    cursor: pointer;

    &::after {
      content: "";
      position: absolute;
      width: 1px;
      height: 14px;
      margin-top: 5px;
      margin-left: 11px;
      background-color: rgb(226, 226, 226);
    }
  }

  button:last-of-type {
    flex-shrink: 0;
    font-size: 18px;
    font-weight: normal;
    line-height: 1.33;
    letter-spacing: normal;
    color: rgb(181, 181, 181);
    cursor: pointer;
    position: relative;
    margin-left: 22px;

    &::after {
      content: "";
      position: absolute;
      display: block;
      background: url("data:image/svg+xml;base64,PHN2ZyB3aWR0aD0iNyIgaGVpZ2h0PSI3IiB2aWV3Qm94PSIwIDAgNyA3IiB4bWxucz0iaHR0cDovL3d3dy53My5vcmcvMjAwMC9zdmciPgogICAgPHBhdGggZD0iTTUuMTA4IDd2LS4wMDdMMS45MTEgMi41NzdWN0guMDQ2VjBoMS44NjdsMy4xOTYgNC40MTlWMGgxLjg2N3Y3SDUuMTA4eiIgZmlsbD0iI0ZBNjIyRiIgZmlsbC1ydWxlPSJldmVub2RkIi8+Cjwvc3ZnPgo=") 0px 0px no-repeat;
      width: 10px;
      height: 10px;
      top: 3px;
      right: -13px;
    }
  }

  button: active, button: hover {
    font-weight: 500;
    color: rgb(95, 0, 128);
  }
`

const SearchWrapper = styled.div`
  position: relative;
  width: 1050px;
  margin: 0px auto;
`

const Search = styled.div<{ scrollFixed: boolean }>`
  display: flex;
  align-items: center;
  justify-content: space-between;
  ${({ scrollFixed }) => 
    scrollFixed 
      ? css`
          position: fixed;
          z-index: 102;
          top: 10px;
          left: 1050px;
          width: 242px;
          height: 36px;
          marign-left: -405px;
          border: none;
          background-color: rgb(247, 247, 247);
          @media screen and (min-width: 1050px) {
            left: 50%;
            margin-left: 120px;
          }
        `
      : css`
          position: absolute;
          top: -55px;
          left: 325px;
          width: 400px;
          height: 48px;
          border: 1px solid rgb(95, 0, 128);
          background-color: rgb(255, 255, 255);
        `}
` 

const SearchInput = styled.input<{ scrollFixed: boolean }>`
  background-color: inherit;
  border:none;
  outline: none;
  letter-spacing: -0.33px;
  color: rgb(51, 51, 51);
  ${({ scrollFixed }) => 
    scrollFixed 
      ? css`
          width: 193px;
          padding-right: 22px; 
          font-weight: 400px;
          line-height: 18px;
          font-size: 12px;
        `
      : css`
          width: 300px;
          font-size: 16px;
        `}
`

const SearchButton = styled.button<{ scrollFixed: boolean }>`
  position: relative;
  height: 30px;
  ${({ scrollFixed }) =>
    scrollFixed
      ? css`
          width: 40px;
          top: 0px;
          margin: 0px 10px 0px 0px;
          background: url("data:image/svg+xml;base64,") 0px 0px no-repeat;
        `
      : css`
          width: 30px;
          bottom: 3px;
          margin: 10px;
          background: url("data:image/svg+xml;base64,") 0px 0px no-repeat
        `}
  
`

const HeaderIconsContainer = styled.div`
  position: relative;
  width: 1050px;
  margin: 0px auto;
`

const HeaderIcons = styled.div<{ scrollFixed: boolean }>`
  display: flex;
  align-items: center;
  ${({ scrollFixed}) =>
    scrollFixed
      ? css`
          position: fixed;
          z-index: 102
          top: 10px;
          right: auto;
          left: 1050px;
          margin-left: -142px;
          @media screen and (min-width: 1050px {
            left: 50%;
            margin=left: 383px;
          })
        `
      : css`
          position: absolute;
          top: -49px;
          right: -6px;
        `}
`

const LocationIcon = styled.div`
  width: 36px;
  height: 36px;
  margin-right: 20px;
  background: url("data:image/svg+xml;base64,") 50% 50% no-repeat;
  cursor: pointer
`

const HeartIcon = styled.button`
  width: 36px;
  height: 36px;
  background: url("data:image/svg+xml;base64,") 50% 50% no-repeat;
`

const CartIconWrapper = styled.div`
  posiiton: relative;
  margin-left: 20px;

  div {
    box-sizing: border-box;
    margin: 0;
  }
`

const CartIcon = styled.button`
  display: block;
  width: 36px;
  height: 36px;
  background: url("data:image/svg+xml;base64,");
`
