import React from 'react'
import { useRef } from 'react'
import styled from 'styled-components'
import { Autoplay, Navigation, Pagination } from 'swiper'
import { Swiper, SwiperSlide } from 'swiper/react'
import { swiperDummy } from '../fakeRepo/swiper/swiperList'
import './swipers.css'

export default function Swipers() {

const _prevBtn = useRef(null);
const _nextBtn = useRef(null);

  return (
    <MainBanners type="MAIN_BANNERS">
      <BannersWrapper>
        <Swiper 
          slidesPerView={1}
          loop={true}
          autoplay={{
            delay: 2500,
            disableOnInteraction: false
          }}
          navigation={{
            nextEl: _nextBtn.current,
            prevEl: _prevBtn.current
          }}
          onBeforeInit={(swiper) => {
            swiper.params.navigation.prevEl = _prevBtn.current;
            swiper.params.navigation.nextEl = _nextBtn.current;
          }}
          pagination={{ type: "fraction" }}
          modules={[Autoplay, Pagination, Navigation]}
        >
          {swiperDummy.map((item) => (
            <SwiperSlide>
              <a
                key={item.id}
                href={item.link}>
                <img
                  key={item.id}
                  src={item.src}
                  alt={item.alt}
                  class="main-banner-image"
                />
              </a>
            </SwiperSlide>
          ))}
        </Swiper>
        <PrevBtn ref={_prevBtn}></PrevBtn>
        <NextBtn ref={_nextBtn}></NextBtn>
      </BannersWrapper>
    </MainBanners>
  )
}

const MainBanners = styled.div`
  height: 100%;
`

const BannersWrapper = styled.div`
  position: relative;
  height: fit-content;
  max-width: 1900px;
  margin: 0px auto 40px;
`

const PrevBtn = styled.button`
  position: absolute;
  top: 0px;
  bottom: 0px;
  right: 50%;
  margin: auto 590px auto 0px;
  z-index: 10;
  width: 52px;
  height: 52px;
  transition: all 0.5s ease 0s;
  opacity: 1;
  background: url("data:image/svg+xml;base64,") 50% 50% no-repeat;
  transform: rotate(180deg);
`

const NextBtn = styled.button`
  position: absolute;
  top: 0px;
  bottom: 0px;
  left: 50%;
  margin: auto 0px auto 590px;
  z-index: 10;
  width: 52px;
  height: 52px;
  transition: all 0.5s ease 0s;
  opacity: 1;
  background: url("data:image/svg+xml;base64,") 50% 50% no-repeat;
`
