import { useCallback, useState } from "react"
import styled from "styled-components"
import axios from "axios"
import { useNavigate } from "react-router-dom"
import { propsType } from "../../components/signup/propsType"
import { useCookies } from "react-cookie"

const SignUp = ({memberId, password, name, email, phone, address, extraAddress, checked, birth }: propsType) => {
  const navigate = useNavigate();
  
  // const doSignUp = async (e: React.MouseEvent<HTMLButtonElement>) => {
  //   e.preventDefault();
  //   try {
  //     await axios
  //       .post(
  //         'http://localhost:5000/api/signup',
  //         {
  //           memberId: memberId,
  //           password: password,
  //           name: name,
  //           email: email,
  //           mobileNumber: phone,
  //           address: address,
  //           extraAddress: extraAddress,
  //           gender: checked,
  //           birth: birth,
  //         }
  //       )
  //       .then((res) => {
  //         console.log(res.data)
  //         window.alert('회원가입이 성공적으로 이루어졌습니다!')
  //       })
  //   } catch (error) {
  //     console.log(error)
  //   }
  // }
  const onSubmit = useCallback(
    async (e: React.MouseEvent<HTMLButtonElement>) => {
      e.preventDefault();
      try {
        await axios
          .post('/http://localhost:5000/api/signup', {
            memberId: memberId,
            password: password,
            name: name,
            email: email,
            mobileNumber: phone,
            address: address,
            extraAddress: extraAddress,
            gender: checked,
            birth: birth,
          })
          .then((res) => {
            console.log('response:', res.data)
            if (res.status === 200) {
              window.alert('회원가입이 성공적으로 이루어졌습니다!')
              navigate('./login');
            }
          })
      } catch (err) {
        console.error(err)
      }
    },
    [memberId, password, name, email, phone, address, extraAddress]
  )
  return (
    <SignUpButton type="submit" onClick={() => onSubmit}>
      <span>가입하기</span>
    </SignUpButton>
  )

}

export default SignUp

const SignUpButton = styled.button`
  display: block;
  padding: 0px 10px;
  text-align: center;
  overflow: hidden;
  width: 240px;
  height: 56px;
  border-radius: 3px;
  color: rgb(255, 255, 255);
  background-color: rgb(95, 0, 128);
  border: 0px none;
  span {
    display: inline-block;
    font-size: 16px;
    font-weight: 500;
  }
`
