import { createStore } from "redux";

const initialState = {
  users: [],
  loggedInuser: null,
  products: [
    {
    id: 1,
    name: 'Pen',
    price: 5,
    },
    {
      id: 2,
      name: 'Pencil',
      price: 1,
    },
    {
      id: 3,
      name: 'Eraser',
      price: 2,
    },
    {
      id: 1,
      name: 'Sharpner',
      price: 4,
    },
  ],
  cart: [],
}

const reducer = (state = initialState, action) => {
  switch(action.type) {
    case "REGISTER":
      return {
        ...state,
        users: [...state.users, action.payload]
      }
    default:
      return state;
  }
}

export default createStore(reducer)
