import { useCallback, useState } from "react"
import styled from "styled-components"
import axios from "axios"
import { useNavigate } from "react-router-dom"
import { propsType } from "../../components/signup/propsType"
import { useCookies } from "react-cookie"

const SignUp = ({memberId, password, name, email, phone, address, extraAddress, checked, birth }: propsType) => {
  const navigate = useNavigate();
  const [cookies, setCookie, removeCookie] = useCookies();
  
  const doSignUp = async (e: React.MouseEvent<HTMLButtonElement>) => {
    e.preventDefault();
    try {
      const { data } = await axios
      .post(
        'http://localhost:5000/api/signup',
        {
          memberId: memberId,
          password: password,
          name: name,
          email: email,
          mobileNumber: phone,
          address: address,
          extraAddress: extraAddress,
          gender: checked,
          birth: birth,
        }
      )
      .then((res) => {
        setCookie('accessToken', data['accessToken'], { path: '/' })
        console.log(data)
        window.alert('회원가입이 성공적으로 이루어졌습니다!')
        navigate('/login');
      })
      
    } catch (error) {
      console.log(error)
    }
  }
