  //           gender: checked,
  //           birth: birth,
  //         })
  //         .then((res) => {
  //           console.log('response:', res)
  //           if (res.status === 200) {
  //             navigate('./signup/success');
  //           }
  //         })
  //     } catch (err) {
  //       console.error(err)
  //     }
  //   },[]
    // [memberId, password, name, email, phone, address, extraAddress, checked, birth]
 
  return (
    <SignUpButton type="submit" onClick={doSignUp}>
      <span>가입하기</span>
    </SignUpButton>
  )

}

export default SignUp

const SignUpButton = styled.button`
  display: block;
  padding: 0px 10px;
  text-align: center;
  overflow: hidden;
  width: 240px;
  height: 56px;
  border-radius: 3px;
  color: rgb(255, 255, 255);
  background-color: rgb(95, 0, 128);
  border: 0px none;
  span {
