import React from 'react'
import styled from 'styled-components'
import SearchPostcode from './SearchPostCode'

export default function Postcode() {
  const {
    isComplete,
    address,
    handleClick,
  } = SearchPostcode()

  return (
    <>
    {!isComplete && 
      <SearchAddress onClick={handleClick}>
        <span>
          <img src="https://res.kurly.com/pc/service/cart/2007/ico_search.svg" />주소 검색
        </span>
      </SearchAddress>}
    {isComplete && 
    <>
    <InputWrapperTop className="InputWrapperTop">
      <InputInnerWrapper className='InputInnerWrapper'>
        <InputField
          name="address"
          placeholder="주소를 검색해주세요"
          type="text"
          value={address}
          readOnly
        />
      </InputInnerWrapper>
    </InputWrapperTop>
    <InputWrapperBottom>
      <InputInnerWrapper>
        <InputField
          name="subAddress"
          placeholder="나머지 주소를 입력해주세요"
          type="text"
        />
      </InputInnerWrapper>
    </InputWrapperBottom>
    </>
      }
    </>
  )
}

const SearchAddress = styled.button`
  display: block;
  padding: 0px 10px;
  text-align: center;
  overflow: hidden;
  width: 100%;
  height: 44px;
  border-radius: 4px;
  color: rgb(95, 0, 128);
  background-color: rgb(255, 255, 255);
  border: 1px solid rgb(95, 0, 128);
  span {
    font-size: 14px;
    font-weight: 500;
    img {
      display: inline-block;
      width: 20px;
      height: 20px;
      margin-right: 4px;
      vertical-align: middle;
    }
  }
`

const InputField = styled.input`
  width: 100%;
  height: 46px;
  padding: 0px 11px 1px 15px;
  border-radius: 4px;
  border: 1px solid rgb(221, 221, 221);
  font-weight: 400;
  font-size: 16px;
  line-height: 1.5;
  color: rgb(51, 51, 51);
  outline: none;
  box-sizing: border-box;
`

const InputWrapperTop = styled.div`
  padding-bottom: 12px;
`

const InputWrapperBottom = styled.div`
  padding-bottom: 0px;
`

const InputInnerWrapper = styled.div`
  position: relative;
  height: 48px;
`
