import { createReducer, createSlice, PayloadAction } from "@reduxjs/toolkit"
import { createAsyncThunk } from "@reduxjs/toolkit"
import axios from "axios";
// import { login } from "../actions/user_action"
// export type UserState = {
//   isLoggedIn: boolean;
//   user: any;
// }

// export type LoginPayload = {
//   memberId: string;
//   password: string;
//   name: string;
// }

const initialState = {
  isLoggedIn: false,
  user: null,
}

export const action = {
  loginAction: createAsyncThunk("USER_LOGIN", async (userData, { rejectWithValue }) => {
    return axios({
      method: 'post',
      url: 'http://localhost:5000/api/login',
      data: userData,
    })
    .then(res => res.data)
    .catch(error => rejectWithValue(error.res.data));
  })
};

export const userSlice = createSlice({
  name: 'user',
  initialState,
  reducers:
  {
    // loginAction: (state: UserState, action: PayloadAction<LoginPayload>) => {
    //   state.isLoggedIn = true;
    //   state.user = action.payload.data;

    // },
    // logoutAction: (state: UserState) => {
    //   state.isLoggedIn = false;
    //   state.user = null;
    // },
  },
  extraReducers: {
    [loginAction.fulfilled]: (state, { payload }) => [...payload]
  }
})

