import React, { useRef, useState } from 'react'
import styled from 'styled-components'
import SubCategory from './SubCategory'




export default function CategoryItems({ items, ulHover }) {

  const [isHovering, setIsHovering] = useState(false);
  const ref = useRef(null);
  let currItem = '';
  let prevItem = '';
  // const [item, setItem] = useState('');
  // const [prevItem, setPrevItem] = useState('');


  // const handleMouseEnter = (e) => {
  //   if (setIsHovering(true)) {
  //     setIsHovering(false);
  //     console.log("hi");
  //     setIsHovering(true);
  //   }
  


  const handleMouseEnter = () => {

    currItem = items.text;
    if (prevItem === '') {
      setIsHovering(true);
    }
    console.log("MouseEnter=======currItem::: ", currItem, "          prevItem:::", prevItem);
  }

  const handleMouseLeave = () => {
 
    if (prevItem === currItem) {
      setIsHovering(true);
    }
    console.log("MouseLeave=======currItem::: ", currItem, "         prevItem:::", prevItem);  
  }

  return (
    <>
      <CategoryItem ref={ref}
        onMouseEnter={handleMouseEnter}
        onMouseLeave={handleMouseLeave}>
        <Container>
          <ItemImg
            key={items.id}
            src={isHovering ? items.hoverImg : items.img}
            alt={items.text} 
          />
          <ItemText>{items.text}</ItemText>
        </Container>
        {(isHovering) ? <SubCategory subMenus={items.subMenu} /> : ""}
      </CategoryItem>
    </>
  )
}

const CategoryItem = styled.li`
  padding: 7px 0px 9px 14px;
  &:hover {
    background-color: rgb(247, 247, 247);
  }
`

const Container = styled.div`
  display: flex;
  flex-wrap: wrap;
  align-items: flex-start;
`

const ItemImg = styled.img`
  flex: 0 1 0%;
  width: 24px;
  height: 24px;
`

const ItemText = styled.span`
  flex: 1 1 0%;
  padding: 1px 20px 0px 10px;
  color: rgb(51, 51, 51);
  font-size: 14px;
  font-weight: 400;
  line-height: 22px;
  ${CategoryItem}:hover & {
    font-weight: 500;
    color: rgb(95, 0, 128);
  }
`
